UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p>\" <strong>ישמח אב</strong> - בראותו אותו במושב החכמים.</p>\r\n<p><strong>בוזה אמו</strong> - כי דרך נשים לגעגע על בניהן ולמנוע מהם שבט מוסר, והבריות מבזין אותם לומר ארור שזה גדל.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `main_qod_subjects` = NULL, `mqorot` = '<br />', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 15 AND `verse_number` = 20 AND 1 LIMIT 1;

