UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p><strong>אולת</strong> - כאשר יעשה <strong>חסר לב אולת</strong>, יחשב בעיניו ל<strong>שמחה</strong>.</p>\r\n<p><strong>ואיש תבונה</strong> - <strong>ישמח</strong> כאשר <strong>ילך</strong> בדרך ה<strong>ישר</strong>.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 15 AND `verse_number` = 21 AND 1 LIMIT 1;

