UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>איש חמה</strong> (מי שדרכו לכעוס) <strong>יגרה</strong> (יחרחר) <strong>מדון</strong> (מריבה) עם בני אדם; &#160; אבל ה<strong>מאריך אף</strong> ואינו ממהר לכעוס, <strong>ישקיט</strong> בזה את המ<strong>ריב</strong>ה.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 15 AND `verse_number` = 18 AND 1 LIMIT 1;

