UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>אורח</strong> - שמורה <strong>אורח חיים</strong> לנפש ה<strong>משכיל</strong> ללכת <strong>למעלה</strong>, אז תשוב אל אלהים, <strong>למע\'ן תסיר</strong> הנפש <strong>מ</strong>לרדת אז אל <strong>שאול מטה</strong> והוא הגיהנם, אבל תשוב אל מקורה אשר חוצבה ממנו.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<br />' WHERE `book` = 'משל' AND `chapter_number` = 15 AND `verse_number` = 24 AND 1 LIMIT 1;

