UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>יהגה</strong> - טרם <strong>יענה</strong>, יחשוב ב<strong>לבו</strong> מה יענה, ועל כו דבריו מעטים. אבל <strong>פי רשעים</strong> ירבה לדבר <strong>רעות</strong> כמעיין ה<strong>נובע</strong>, כי ידבר כל העולה על רוחו ואינו חושש.</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>יהגה</strong> - יחשוב, כמו ():</span> <strong>והגיון לבי</strong>.</p>\r\n<p><strong>לענות</strong> - מלשון עניה ותשובה.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<br />', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 15 AND `verse_number` = 28 AND 1 LIMIT 1;

