UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p><strong>רחוק וגו\'</strong> - מלשמוע את תפילתם.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 15 AND `verse_number` = 29 AND 1 LIMIT 1;

