UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>בכל מקום</strong>, אף אם יסתרו במסתרים, <strong>צופות</strong> (מביטות) <strong>עיני ה\'</strong> לראות מעשה ה<strong>רעים</strong> וה<strong>טובים</strong>, ולא נכחד ממנו.</p>', `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 15 AND `verse_number` = 3 AND 1 LIMIT 1;

