UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p>מי ש<strong>לשונו רפויה</strong> ולא ידבר קשות ורק ידבר רכות, היא לו ל<strong>עץ</strong> המגדל <strong>חיים</strong>; &#160; אבל המ<strong>סלף</strong> (מעקם) בלשונו לדבר קשות, בא <strong>שבר ברוחו </strong>(רצונו), כי לא יקובל דבריו ולא ימולא רצונו.</p>', `tirgum` = '<p>\r\n<strong>לשון המרפאת</strong>    בדבריה את כאבי-הנפש של הזולת, עשויה להציל אותו ממוות או התאבדות כמו <strong>עץ חיים</strong>;&#160;&#160; אולם \r\n<strong>סילוף</strong> (עיוות) במילים או בטון הדיבור  עלול לדכא את השומע ו<strong>לשבור</strong>    את \r\n<strong>רוחו</strong>    כמו עץ \r\n<strong>הנשבר</strong>  \r\n<strong>ברוח</strong>  חזקה.</p>   \r\n<p class=\"future\">/ כדי \r\n<strong>לרפא</strong>    את \r\n<strong>לשונו</strong>    של אדם המתקשה בדיבור, יש לחבר אותו \r\n<strong>לעץ החיים</strong>    של נשמתו, שהרי \r\n<strong>הסילוף בלשון</strong>    נובע \r\n<strong>משבר ברוחו</strong>    של האדם.</p>' WHERE `book` = 'משל' AND `chapter_number` = 15 AND `verse_number` = 4 AND 1 LIMIT 1;

