UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>בית צדיק חוסן</strong> (חוזק) <strong>רב</strong>, מחוזק הוא מאד ולא יחרב;&#160;&#160;&#160; <strong>ו</strong>בעת <strong>בוא</strong> בו ה<strong>רשע</strong>, <strong>נעכר</strong> (נשחת) הבית ההוא.</p>', `tirgum` = '<p>\r\n<strong>הצדיק</strong> <strong>מאחסן</strong> ב<strong>ביתו</strong> רכוש <strong>רב</strong> שאסף ביושר; &#160; אולם אם הצדיק יאפשר לרשע להביא רכוש אל ביתו - \r\n<strong>בגלל תבואתו</strong>   של \r\n<strong>הרשע</strong>   גם תבואת הצדיק \r\n<strong>נעכרת</strong>   (מתקלקלת).</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 15 AND `verse_number` = 6 AND 1 LIMIT 1;

