UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>שפתי חכמים</strong> המה <strong>יזרו</strong> (מפזרים) את ה<strong>דעת</strong> ללמד לכל;&#160;&#160; <strong>ולב כסילים</strong> - <strong>לא </strong>יחפצו ללמוד <strong>כן</strong> כאשר החכמים חפצים ללמד.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 15 AND `verse_number` = 7 AND 1 LIMIT 1;

