UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p>המדבר רכות ו<strong>מחשבותיו לרעה</strong> הוא <strong>תועבת ה\'</strong>;&#160;&#160;&#160; <strong>ו</strong>ראוים <strong>אמרי נועם</strong> להיות <strong>טהורים</strong> מבלי תערובות מחשבה רעה.</p>\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 15 AND `verse_number` = 26 AND 1 LIMIT 1;

