UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>לב צדיק, </strong>טרם <strong>יענה</strong>, <strong>יהגה</strong> (יחשוב) ב<strong>לבו</strong> מה יענה, ועל-כן דבריו מעטים; &#160; אבל <strong>פי רשעים</strong> ירבה לדבר <strong>רעות</strong> כמעיין ה<strong>נובע</strong>, כי ידבר כל העולה על רוחו ואינו חושש.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 15 AND `verse_number` = 28 AND 1 LIMIT 1;

