UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>יד ליד</strong> - מ<strong>יד</strong>ו של מקום יבוא הגמול <strong>ליד</strong> המחויב, ו<strong>לא ינקה</strong> ממנה כי לא תשוב ריקם.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<br />', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 16 AND `verse_number` = 5 AND 1 LIMIT 1;

