UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p><strong>לב אדם יחשב </strong>באיזה <strong>דרך</strong> יהלך,&#160;&#160; <strong>וה\'&#160;מכין</strong> פסיעותיו ללכת במקום שירצה.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `tirgum` = '<p>\r\nעל <strong>האדם</strong> המתכונן למסע <strong>לחשב</strong> <strong>בליבו</strong>  ולתכנן היטב את <strong>דרכו</strong>;&#160;&#160; אולם רק \r\n<strong>ה\'</strong> יחליט אם <strong>יכין</strong> (יתמוך וייצב) כל <strong>צעד </strong>שיעשה האדם בדרך זו.</p>' WHERE `book` = 'משל' AND `chapter_number` = 16 AND `verse_number` = 9 AND 1 LIMIT 1;

