UPDATE `sgulot` SET `prefix` = NULL, `tirgum` = '<p>הדיבור הנמצא \r\n<strong>על שפתי מלך</strong>   דומה \r\n<strong>לקסם</strong>   - יש לו כוחות מופלאים ומתוחכמים לזהות את האמת \r\n<strong>במשפט</strong>;&#160;&#160;&#160; ועליו להיזהר במיוחד, כאשר הוא עוסק \r\n<strong>במשפט</strong>, <strong>לא \r\nלמעול</strong>   בדברי <strong>פיו</strong> - לא לנצל את כוחו לרעה.</p><p>/ כשהמלך נושא \r\n<strong>קסם</strong>  (שבועה) \r\n<strong>על שפתיו</strong>, ה\' יעמיד אותו <strong>במשפט</strong> על קיום השבועה, ולכן עליו להיזהר ש<strong>לא ימעל פיו</strong> ולא יפר את שבועתו.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 16 AND `verse_number` = 10 AND 1 LIMIT 1;

