UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>באור</strong> - כשה<strong>מלך מאיר פניו</strong> למי, ומראה פנים צהובות ושוחקות, הוא לו ל<strong>חיים</strong>, כי הרבה טובה ביד המלך.</p>\r\n<p><strong>ורצונו</strong> - <strong>רצון</strong> המלך הוא <strong>כעב</strong> המביא <strong>מלקוש</strong>, הוא המטר המאוחר היורד על הקשין ועל המלילות.</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>מלקוש</strong> - כן נקרא המטר המאוחר, כמו ():</span> <strong>יורה ומלקוש</strong>.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<br />', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 16 AND `verse_number` = 15 AND 1 LIMIT 1;

