UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>מה טוב</strong> - <strong>מה</strong> מאוד הוא <strong>טוב</strong> יותר <strong>מחרוץ</strong>, והוא מין זהב טוב.</p>\r\n<p><strong>וקנות בינה</strong> - קנין הבינה.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 16 AND `verse_number` = 16 AND 1 LIMIT 1;

