UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>מסלת</strong> - דרך ה<strong>ישרים</strong> הוא ל<strong>סור מרע</strong>, כי יתבוננו בדרכם שלא יבואו לידי נדנוד עוון. והרוצה <strong>לשמור נפשו, ישמור דרכו</strong> לבלי לכת כפי ההזדמן.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 16 AND `verse_number` = 17 AND 1 LIMIT 1;

