UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>לפני שבר</strong> - טרם יבוא <strong>שבר</strong> על הרשע בא לו מתחלה <strong>גאון</strong> וממשל רב, כי בזה יכפל הצער.</p>\r\n<p><strong>גובה רוח</strong> - על רוב הטובה הבאה לו טרם הכשלון. וכפל הדבר במ\"ש. .</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>גאון</strong> - ענינו ממשלה, כמו (): <strong>גאון יעקב</strong>.</p>\r\n<p><strong>כשלון</strong> - ענין חלשות ונפילת הכח.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 16 AND `verse_number` = 18 AND 1 LIMIT 1;

