UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p>טרם יבוא <strong>שבר</strong> על הרשע, בא לו מתחלה <strong>גאון</strong> וממשל רב, כי בזה יכפל הצער;&#160;&#160;&#160; <strong>וטרם כישלון</strong> בא לו <strong>גובה רוח</strong> על רוב הטובה הבאה לו.</p>', `dquyot` = '<p>\"<q class=\"mfrj\"><strong>גאון</strong> - ענינו ממשלה, כמו גאון יעקב</q>\" \"<q class=\"mfrj\"><strong>כשלון</strong> - ענין חלשות ונפילת הכח</q>\"<small class=\"small\"> (מצודת ציון)</small>.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 16 AND `verse_number` = 18 AND 1 LIMIT 1;

