UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>לחכם לב</strong> - למי שלמד <strong>חכמה</strong> מרבו, סופו <strong>יקרא נבון</strong>, כי יתחכם להבין דבר מתוך דבר.</p>\r\n<p><strong>ומתק</strong> - ה<strong>ממתיק</strong> אמרים לזולת, למען יערב לו להיות מקובל על הלב, הנה יועיל גם לעצמו, כי בזה <strong>יוסיף</strong> ללמוד להבין תוכיות הדבר.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<br />', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 16 AND `verse_number` = 21 AND 1 LIMIT 1;

