UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>יש דרך ישר לפני איש</strong> (לפי ראות עיניו), <strong>ואחרית</strong> הדרך ההיא הולך אל <strong>דרכי מות</strong>.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<p>   פסוקנו זהה ל<a class=\"psuq\" href=\"http://localhost/tnk1/prqim/t2814.htm#12\">משלי יד12</a>, אך פירשנו אותם באופן שונה לפי ההקשר.</p><p> שם, פירשנו ש<strong>איש</strong> הוא האדם התמים הצועד בדרך:&#160;&#160;&#160; הדרך נראית ישרה רק כאשר היא נמצאת <strong>לפני האיש</strong> (כשעדיין לא החל לצעוד בה), אך כשהוא מגיע ל<strong>אחרית</strong> הדרך הוא מגלה בה <strong>דרכי מוות</strong>.</p><p>כאן, פירשנו ש<strong>איש</strong> הוא האדם הרע האורב בהמשך הדרך, וזאת על-פי הפסוקים 27-29, שכולם פותחים במילה <strong>איש</strong> ומתארים אנשים מסוכנים:&#160;&#160;&#160;&#160; הדרך היא ישרה רק \r\n<strong>\r\nלפני</strong>    שמגיעים \r\nאל אותו <strong>איש</strong>; אבל כשמגיעים <strong>\r\nלאחרית</strong>   הדרך, מגלים שם שלוש דרכים וכולן \r\n<strong>דרכי מוות</strong>, כי בכל אחת מהן עומד \r\n<strong>איש</strong>       רע ומסוכן.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 16 AND `verse_number` = 25 AND 1 LIMIT 1;

