UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>איש בליעל כורה </strong>(חופר ומחפש) תחבולות לעשות <strong>רעה</strong> לבני אדם, וגם ירבה לדבר דברי חדודים ומכעיסים כאילו <strong>על שפתיו אש צורבת </strong>(שורפת).\r\n\r\n\r\n\r\n</p>', `main_qod_subjects` = NULL, `mqorot` = '<br />', `hqblot` = '<p><strong>צרבת</strong> היא כויה הנובעת משחין, <a class=\"psuq\" href=\"/tnk1/prqim/t0313.htm#23\">ויקרא יג23</a>: \"<q class=\"psuq\">וְאִם תַּחְתֶּיהָ תַּעֲמֹד הַבַּהֶרֶת, לֹא פָשָׂתָה - <strong>צָרֶבֶת</strong> הַשְּׁחִין הִוא, וְטִהֲרוֹ הַכֹּהֵן</q>\": \"<q class=\"mfrj\">כי הפיתוי שלו דומה <strong>לאש</strong> של <strong>צרבת</strong> המכווה, שמצער תמיד את הנכווה, עד שיירפא לגמרי ממכתו, כך פיתויו של הכוח החיצון אינו מניח <strong>לנפש העמל</strong>, עד שיתגבר עליו בכוח <strong>עמלו</strong>, וישלים את תיקונו</q>\"<small class=\"small\"> (רמ\"ד ואלי)</small>.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 16 AND `verse_number` = 27 AND 1 LIMIT 1;

