UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>איש חמס יפתה רעהו</strong> לאחוז גם הוא בדרכי חמס, <strong>ומוליכו בדרך לא טוב</strong> למצוא חבר לעצמו.</p>\r\n', `tirgum` = '<p><strong>איש חמס</strong> (מושחת) <strong>מפתה</strong> את <strong>רעהו</strong> בהדרגה: בשלב ראשון הוא משכנע אותו <strong>ללכת</strong> <strong>בדרך </strong>שהיא רק מעט <strong>לא טובה</strong> -</p>', `main_qod_subjects` = NULL, `mqorot` = '<h3>מ<strong>לא טוב</strong> ל<strong>רעה</strong></h3><p>\"<q class=\"mfrj\">כן דרכו של יועץ בליעל, לתת עצה בתחילה, שאינה <strong>לא טובה</strong>\r\n ולא רעה לגמרי, שלא יחשדהו שומע ויברח מעצתו ומחברתו.&#160;&#160;&#160; ואחר כך, כשרואה \r\nשהוא מקבל את עצתו הבינונית, מעיז את פניו לתת לו העצה רעה הגמורה, והשומע \r\nלא ימאס בה מאחר שכבר קיבל את הבינונית, כמו שאמר המלך החסיד</q>\", <a class=\"psuq\" href=\"http://localhost/tnk1/prqim/t2636.htm#5\">תהלים לו5</a>: \"<q class=\"psuq\">אָוֶן יַחְשֹׁב עַל מִשְׁכָּבוֹ, יִתְיַצֵּב עַל דֶּרֶךְ <strong>לֹא טוֹב</strong>, רָע לֹא יִמְאָס</q>\", \"<q class=\"mfrj\">כי הדברים הולכים בהדרגה. וזהו שאמר בכאן בתחילה: <strong>והוליכו בדרך לא טוב</strong>, ואחר-כך אמר בסמוך</q>\", <a class=\"psuq\" href=\"http://localhost/tnk1/prqim/t2816.htm#30\">משלי טז30</a>: \"<q class=\"psuq\">עֹצֶה עֵינָיו לַחְשֹׁב תַּהְפֻּכוֹת, קֹרֵץ שְׂפָתָיו <strong>כִּלָּה רָעָה</strong></q>\" (<a href=\"http://localhost/tnk1/ktuv/mj/16-30.html\">פירוט</a>) - \"<q class=\"mfrj\">שרומז אל ה<strong>עצה</strong> רעה הגמורה שנותן לו לבסוף</q>\"<small class=\"small\"> (רמ\"ד ואלי)</small>.</p>', `dquyot` = '<h3>מ<strong>לא טוב</strong> ל<strong>רעה</strong></h3><p>\"<q class=\"mfrj\">כן דרכו של יועץ בליעל, לתת עצה בתחילה, שאינה <strong>לא טובה</strong> ולא רעה לגמרי, שלא יחשדהו שומע ויברח מעצתו ומחברתו.&#160;&#160;&#160; ואחר כך, כשרואה שהוא מקבל את עצתו הבינונית, מעיז את פניו לתת לו העצה רעה הגמורה, והשומע לא ימאס בה מאחר שכבר קיבל את הבינונית, כמו שאמר המלך החסיד</q>\", <a class=\"psuq\" href=\"/tnk1/prqim/t2636.htm#5\">תהלים לו5</a>: \"<q class=\"psuq\">אָוֶן יַחְשֹׁב עַל מִשְׁכָּבוֹ, יִתְיַצֵּב עַל דֶּרֶךְ <strong>לֹא טוֹב</strong>, רָע לֹא יִמְאָס</q>\", \"<q class=\"mfrj\">כי הדברים הולכים בהדרגה. וזהו שאמר בכאן בתחילה: <strong>והוליכו בדרך לא טוב</strong>, ואחר-כך אמר בסמוך</q>\", <a class=\"psuq\" href=\"/tnk1/prqim/t2816.htm#30\">משלי טז30</a>: \"<q class=\"psuq\">עֹצֶה עֵינָיו לַחְשֹׁב תַּהְפֻּכוֹת, קֹרֵץ שְׂפָתָיו <strong>כִּלָּה רָעָה</strong></q>\" (<a href=\"/tnk1/ktuv/mj/16-30.html\">פירוט</a>)<small class=\"small\"> (רמ\"ד ואלי)</small>.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 16 AND `verse_number` = 29 AND 1 LIMIT 1;

SET character_set_client=utf8;
