UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>בחיק</strong> - דרך <strong>מטילי גורל</strong> להניח פתקין <strong>בחיק</strong> או במקום נסתר זולתו, ומשם יקחום להניחם על המנות לדעת מה למי. ואמר בזה, אל תהרהרו על הגורל לומר שבא בקרי והזדמן, כי בעוד הטילו פתקי הגורל אל החיק, עד לא הוציאום, כבר נגזר <strong>משפט</strong> הדבר <strong>מה\'</strong>, וזכה כל אחד בחלקו אשר חלק לו אלוה ממעל.</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>יוטל</strong> - ענין השלכה.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<br />' WHERE `book` = 'משל' AND `chapter_number` = 16 AND `verse_number` = 33 AND 1 LIMIT 1;

