UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p><strong>איש חמס יפתה רעהו</strong> לאחוז גם הוא בדרכי חמס, <strong>ומוליכו בדרך לא טוב</strong> למצוא חבר לעצמו.</p>\r\n', `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 16 AND `verse_number` = 29 AND 1 LIMIT 1;

