UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>\r\nטוב ארך אפיים מגבור</strong> - כי הגבור לא יתגבר כי אם במעשה הגבורה, והמאריך אף יתגבר על כל ב\'שב ואל תעשה\';&#160;&#160;&#160; <strong>ומושל ברוחו</strong> (מי שבידו לכבוש כעסו) הוא טוב<strong> מ</strong>גבור ה<strong>לוכד עיר</strong> מבצר. <small class=\"small\">וכפל הדבר במילים שונות.</small></p>' WHERE `book` = 'משל' AND `chapter_number` = 16 AND `verse_number` = 32 AND 1 LIMIT 1;

