UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p>הלא הגדר לא יזיק, כי עוד יועיל, ותהיה <strong>רפאות לשרך</strong> (הוא הטבור) <strong>ושיקוי לעצמותיך</strong> (הוא המוח המשקה העצמות), כלומר: יחזיק אותך בקיום המצוות.\r\n\r\n\r\n\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 3 AND `verse_number` = 8 AND 1 LIMIT 1;

