UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p>ה<strong>כל</strong> ברא <strong>ה\'</strong> <strong>למען</strong> קלוסו, <strong>וגם</strong> טובת ה<strong>רשע</strong> וכח הזרוע שנתן בו הוא להיות מוכן לרצועת מרדות ב<strong>יום</strong> בוא ה<strong>רעה</strong> להיות שבט אפו, ואם כן גם זה הוא למענהו.</p>\r\n', `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 16 AND `verse_number` = 4 AND 1 LIMIT 1;

