UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p>יותר <strong>טוב</strong> הון <strong>מעט</strong> הבא <strong>בצדק</strong> וביושר, <strong>מהרבה תבואות</strong> הנאספים <strong>בלא משפט</strong> כי אם בגזל ובעושק.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 16 AND `verse_number` = 8 AND 1 LIMIT 1;

