UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p>יותר <strong>טוב</strong> להיות <strong>שפל רוח</strong> להתחבר עם ה<strong>ענוים</strong>, אף אם מעט ירויח עמהם, מלהיות <strong>מחלק שלל</strong> (הון) רב מאשר ירויח עם הגאים, כי פן ילמד ממעשיהם.\r\n\r\n\r\n\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 16 AND `verse_number` = 19 AND 1 LIMIT 1;

