SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p>ה<strong>שכל</strong> הוא ל<strong>בעליו</strong> כמעיין הנובע <strong>חיים</strong>, כי בעבורו בא לו חיים;&#160;&#160;&#160; וה<strong>יסורים</strong> הבאים על ה<strong>אוילים</strong> הם בעבור <strong>איוולתם</strong>.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `tirgum` = '<p><strong>השכל</strong> הבוחן כל דבר לעומק הוא \r\n<strong>מקור חיים</strong>  עבור \r\n<strong>בעליו</strong>;&#160;&#160; אולם <strong>מוסר</strong>-ההשכל שמפיקים אנשים \r\n<strong>אוילים</strong>, שלא בדקו את הדברים לעומק, הוא \r\n<strong>איוולת</strong>  - שטחי ולא רציני.</p><p>/ <strong>השכל</strong>, החושב איך להצליח בעתיד, הוא <strong>מקור חיים לבעליו</strong>;&#160;&#160;&#160; אולם <strong>מוסר</strong> הכליות של <strong>האוילים</strong>, המתייסרים על טעויות העבר, הוא <strong>איוולת</strong> ואינו מקדם אותם לשום מקום.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 16 AND `verse_number` = 22 AND 1 LIMIT 1;

