UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>לב חכם ישכיל</strong> (ילמד) את <strong>פיו</strong> להמתיק אמרים, וכאשר יהיו שגורים <strong>על שפתיו - יוסיף</strong> עוד לימוד להבין תוכיות הדבר.&#160;&#160; אם כן, באה ההשכלה מן הלב אל הפה וחוזרות לבוא מן הפה אל הלב עד אין תכלית.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 16 AND `verse_number` = 23 AND 1 LIMIT 1;

