UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p><strong>יש דרך ישר לפני איש</strong> (לפי ראות עיניו), <strong>ואחרית</strong> הדרך ההיא הולך אל <strong>דרכי מות</strong>.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 16 AND `verse_number` = 25 AND 1 LIMIT 1;

