UPDATE `sgulot` SET `prefix` = NULL, `hqblot` = '<p>1. גם עשו, אחי יעקב,&#160;עשה עסקה גרועה מתוך רעב, כשמכר את בכורתו תמורת \r\n<a href=\"/tnk1/klli/mdrjim/nzid.html\">נזיד עדשים</a>. \r\n</p>         \r\n<p>2. בספר קהלת ישנו פסוק דומה מאד לפסוקנו; נציג את הפסוקים זה לצד זה, ונשאיר לקוראים להסיק מסקנות: \r\n</p>         \r\n<ul><li>\r\n<a class=\"psuq\" href=\"/tnk1/prqim/t2816.htm#26\">משלי טז26</a>: \"<q class=\"psuq\"><strong>נֶפֶשׁ</strong>   \r\n<strong>     \r\nעָמֵל</strong>        עָמְלָה לּוֹ, כִּי אָכַף עָלָיו \r\n<strong>\r\nפִּיהוּ</strong></q>\"</li><li>\r\n<a class=\"psuq\" href=\"/tnk1/prqim/t3106.htm#7\">קהלת ו7</a>: \"<q class=\"psuq\">כָּל \r\n<strong>\r\nעֲמַל</strong>        הָאָדָם \r\n<strong>\r\nלְפִיהוּ</strong>,      וְגַם \r\n<strong>\r\nהַנֶּפֶשׁ</strong>        לֹא תִמָּלֵא</q>\"\r\n</li></ul>   \r\n<p class=\"future\">        שלושת הפסוקים הבאים (משלי טז27-29) מדברים על \r\nשלושה סוגים של אנשים רעים; ייתכן שהפסוק שלנו מזהיר את האדם, שלפני שהוא \r\nבוחר מקום עבודה כדי להשביע את רעבונו, יברר שבמקום העבודה אין אנשים רעים \r\nשעלולים להשחית את נפשו. ראו: \r\n<a href=\"/tnk1/ktuv/mj/16-25.html\">זהירות בבחירת מקום עבודה</a>.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 16 AND `verse_number` = 26 AND 1 LIMIT 1;

SET character_set_client=utf8;
SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p><strong>איש בליעל כורה </strong>(חופר ומחפש) תחבולות לעשות <strong>רעה</strong> לבני אדם, וגם ירבה לדבר דברי חדודים ומכעיסים כאילו <strong>על שפתיו אש צורבת </strong>(שורפת).\r\n\r\n\r\n\r\n</p>', `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 16 AND `verse_number` = 27 AND 1 LIMIT 1;

SET character_set_client=utf8;
SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p><strong>איש </strong>ה<strong>מהפך</strong> אמרים ממה שנאמרו, הוא מגרה מריבה בין הבריות;&#160;&#160; <strong>ונרגן</strong>, המתלונן אחר מעשה בני אדם, <strong>מפריד</strong> מן האנשים את <strong>האלוף</strong> (השר) אשר עליהם, כי מורדים בו וגם הוא נהפך להם לאויב.\r\n\r\n\r\n\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 16 AND `verse_number` = 28 AND 1 LIMIT 1;

SET character_set_client=utf8;
SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>איש חמס יפתה רעהו</strong> לאחוז גם הוא בדרכי חמס, <strong>ומוליכו בדרך לא טוב</strong> למצוא חבר לעצמו.</p>\r\n', `main_qod_subjects` = NULL, `mqorot` = '<p>\"<q class=\"mfrj\">כן דרכו של יועץ בליעל, לתת עצה בתחילה, שאינה <strong>לא טובה</strong>\r\n ולא רעה לגמרי, שלא יחשדהו שומע ויברח מעצתו ומחברתו.&#160;&#160;&#160; ואחר כך, כשרואה \r\nשהוא מקבל את עצתו הבינונית, מעיז את פניו לתת לו העצה רעה הגמורה, והשומע \r\nלא ימאס בה מאחר שכבר קיבל את הבינונית, כמו שאמר המלך החסיד</q>\", <a class=\"psuq\" href=\"http://localhost/tnk1/prqim/t2636.htm#5\">תהלים לו5</a>: \"<q class=\"psuq\">אָוֶן יַחְשֹׁב עַל מִשְׁכָּבוֹ, יִתְיַצֵּב עַל דֶּרֶךְ <strong>לֹא טוֹב</strong>, רָע לֹא יִמְאָס</q>\", \"<q class=\"mfrj\">כי הדברים הולכים בהדרגה. וזהו שאמר בכאן בתחילה: <strong>והוליכו בדרך לא טוב</strong>, ואחר-כך אמר בסמוך</q>\", <a class=\"psuq\" href=\"http://localhost/tnk1/prqim/t2816.htm#30\">משלי טז30</a>: \"<q class=\"psuq\">עֹצֶה עֵינָיו לַחְשֹׁב תַּהְפֻּכוֹת, קֹרֵץ שְׂפָתָיו <strong>כִּלָּה רָעָה</strong></q>\" (<a href=\"http://localhost/tnk1/ktuv/mj/16-30.html\">פירוט</a>) - \"<q class=\"mfrj\">שרומז אל ה<strong>עצה</strong> רעה הגמורה שנותן לו לבסוף</q>\"<small class=\"small\"> (רמ\"ד ואלי)</small>.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 16 AND `verse_number` = 29 AND 1 LIMIT 1;

SET character_set_client=utf8;
SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p>ה<strong>עוצה</strong> (סותם) <strong>עיניו לחשוב להפך</strong> אמרים<small class=\"small\"> (כי דרך המעמיק לחשוב מה, יסתום אז עיניו, לבל יבלבלו אותו דברים הנראים)</small>;&#160;&#160;&#160;&#160; ה<strong>קורץ</strong> (מנדנד) ב<strong>שפתיו</strong> לרמז לשון הרע,&#160;&#160; הנה זה <strong>כילה</strong> (גמר) ה<strong>רעה</strong> אשר חשב עליה, כי רמזה ועשה מעשה.\r\n\r\n\r\n\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 16 AND `verse_number` = 30 AND 1 LIMIT 1;

SET character_set_client=utf8;
SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p>ההולך <strong>בדרך צדקה, תמצא</strong> בו <strong>עטרת תפארת שיבה</strong>, כי ירבה ימים בגדולה ותפארת.</p>\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 16 AND `verse_number` = 31 AND 1 LIMIT 1;

