UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n<p><strong>אל תהי חכם בעיניך</strong> להקל בדבר הנאסר לעשות גדר ושימור, בחושבך שאין חכם כמותך צריך גדר, אלא <strong>ירא את ה\'</strong> פן עם כל חכמתך תיכשל, <strong>וסור</strong> (הרחק מאד) <strong>מרע</strong> ועשה גדר.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 3 AND `verse_number` = 7 AND 1 LIMIT 1;

