UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>ו</strong>לא תחסר כלום בעבור זה, כי <strong>ימלאו אסמיך</strong> (אוצרות התבואה)<strong> שבע</strong> - הברכה תשולח בהם ועוד תוסיף, <strong>והתרוש</strong> היינות אשר ב<strong>יקביך</strong> (הבורות אשר היין יורד בהם) <strong>יפרצו</strong> (יתרבו מאד).</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 3 AND `verse_number` = 10 AND 1 LIMIT 1;

