UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>כי טוב</strong> מסחר החכמה ממ<strong>סחר</strong> ה<strong>כסף</strong>: כי מי שנוטל מחיר הכסף, לא ישאר הכסף בידו ולא המחיר ביד האחר; ולא כן בדבר החכמה, כי מי שלומד עם אחר חכמה אחת במחיר למוד חכמה אחרת, אחר-כך שניהם ביד כל אחד ואחד;&#160;&#160;&#160;&#160; ו<strong>תבואתה</strong> (פרי החכמה, כלומר שהבין מדעתו מתוך דבר החכמה שלמד), הנה <strong>טובה</strong> היא <strong>מחרוץ</strong> (זהב טוב).\r\n\r\n\r\n\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 3 AND `verse_number` = 14 AND 1 LIMIT 1;

