UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>למחזיקים</strong> (לאוחזים) בה, היא להם ל<strong>עץ חיים</strong>, כי בעבורה ישיג החיים;&#160;&#160;&#160; ו<strong>התומך</strong> אותה בגדרים וסייגים הוא <strong>מאושר</strong> (משובח).</p>', `main_qod_subjects` = NULL, `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 3 AND `verse_number` = 18 AND 1 LIMIT 1;

