UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>בני! </strong>הואיל וחשובה היא כל-כך, מהראוי ש<strong>אל ילוזו </strong>(יסורו)<strong> מנגד עיניך</strong>;&#160; <strong>נצור תושיה ומזמה</strong> (שמור התורה במעשה ובמחשבה) -\r\n\r\n\r\n\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 3 AND `verse_number` = 21 AND 1 LIMIT 1;

