SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p>- <strong>ו</strong>שמירת המעשה והמחשבה <strong>יהיו</strong> סיבה להביא <strong>חיים לנפשך</strong>, ולהלביש <strong>צוארך</strong> ב<strong>חן</strong>.</p>\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 3 AND `verse_number` = 22 AND 1 LIMIT 1;

SET character_set_client=utf8;
SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p>- כשתהיה נשמר במעשה ובמחשבה, <strong>אז תלך לבטח בדרכיך</strong>, כי אין מחריד;&#160;&#160;&#160; <strong>ורגלך לא תגוף</strong> (תוכה) באבני מכשול המושלכים בדרך, כלומר: לא תאונה אליך רעה.\r\n\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 3 AND `verse_number` = 23 AND 1 LIMIT 1;

SET character_set_client=utf8;
SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>אם תשכב לא תפחד, ושכבת - וערבה</strong> (מתקה)<strong> שנתך</strong>, כדרך הישן בוטח מבלי פחד.</p>\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 3 AND `verse_number` = 24 AND 1 LIMIT 1;

SET character_set_client=utf8;
SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>אל תירא מפחד פתאום</strong> - כי לא תבא אליך, <strong>וכאשר תבוא שואת</strong> (חשכת) <strong>הרשעים</strong> - לא תירא מזה, כי לא תגע בך לרעה.\r\n\r\n\r\n\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 3 AND `verse_number` = 25 AND 1 LIMIT 1;

SET character_set_client=utf8;
SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>כי ה\' יהיה בכסלך</strong> - במה שתבטח ותקוה אליו, והוא <strong>ישמור רגלך מן הלכידה</strong>.\r\n\r\n\r\n\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 3 AND `verse_number` = 26 AND 1 LIMIT 1;

SET character_set_client=utf8;
SET character_set_connection=utf8;
UPDATE `sgulot` SET `mcudot` = '<p><strong>אל תמנע טוב</strong> <strong>מבעל</strong>-הטובה (ממי שבא לקבל הטובה), <strong>ב</strong>עת <strong>היות</strong> <strong>אֵל</strong> (כח ואמצות) <strong>בידך</strong> לעשות הטובה.\r\n\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 3 AND `verse_number` = 27 AND 1 LIMIT 1;

SET character_set_client=utf8;
SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>אל תאמר לרעך</strong>, המבקש הטובה, <strong>לך</strong> היום <strong>ושוב</strong> למחר ואז אתן את שאלתך, אם דבר-הטובה <strong>יש עמך</strong> ותוכל לתת מיד.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 3 AND `verse_number` = 28 AND 1 LIMIT 1;

SET character_set_client=utf8;
SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>אל תחרוש</strong> (תחשוב) <strong>על רעך רעה</strong>, <strong>ו</strong>הלא <strong>הוא</strong> יושב אתך לבטח, בוטח בך כאיש על רעהו, ולמה תחשוב עליו רעה?!\r\n\r\n\r\n\r\n</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 3 AND `verse_number` = 29 AND 1 LIMIT 1;

SET character_set_client=utf8;
SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>אל תריב עם אדם חינם, אם לא גמלך </strong>(התחיל לעשות עמך)<strong> רעה</strong>.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<p>הפסוק אוסר לריב עם אדם <strong>חינם</strong>, ומסביר באיזה מקרה הריב הוא חינם - כאשר הצד השני <strong>לא גמלך רעה</strong>, כלומר מותר לריב רק עם אדם שגמל לנו רעה. למה בדיוק הכוונה?</p><p>1. מותר לריב רק עם אדם שפגע בנו: \"<q class=\"mfrj\"><strong>אל תריב עם אדם</strong>&#160;- להתלונן עליו,&#160;<strong>אם לא גמלך רעה</strong>&#160;- שעבר על המצוה הכתובה בתורה \"ואהבת לרעך כמוך\"; ומי שהוא רשע - רשאי אתה לשונאו</q>\"&#160;<small>(<a href=\"http://he.wikisource.org/wiki/%D7%A7%D7%98%D7%A2:%D7%A8%D7%A9%22%D7%99_%D7%A2%D7%9C_%D7%9E%D7%A9%D7%9C%D7%99_%D7%92_%D7%9C\" class=\"external text\">רש\"י</a></small><small>)</small>.&#160; \"<q class=\"mfrj\">גם תחילת המעשה נקרא במקרא בלשון גמול, וכן</q>\" <a class=\"psuq\" href=\"http://tora.us.fm/tnk1/prqim/t1404.htm#4\">יואל ד4</a>: \"<q class=\"psuq\">וְאִם <strong>גֹּמְלִים</strong> אַתֶּם עָלָי</q>\" <small>(</small><small><a href=\"http://he.wikisource.org/wiki/%D7%A7%D7%98%D7%A2:%D7%9E%D7%A6%D7%95%D7%93%D7%95%D7%AA_%D7%A2%D7%9C_%D7%9E%D7%A9%D7%9C%D7%99_%D7%92_%D7%9C\" class=\"external text\">מצודות</a>)</small>.</p><p>2. מותר לריב רק עם אדם שניסה לפגוע בנו בתחום הרוחני, להחטיא אותנו: \"<q class=\"mfrj\"><strong>אל תריב עם אדם</strong>, אף אם הזיקך ממון,&#160;<strong>אם לא גמלך רעה</strong>, והיינו שהחטיאך. שכל המחטיא חברו כאילו הורגו</q>\"&#160;<small>(<a href=\"http://localhost/daat/mahshevt/kitsur/10.htm\">קיצור ספר חרדים, פרק י: מצות לא תעשה מדרבנן התלויים בדבור פה</a>)</small>.</p><p>3. ולענ\"ד, <strong>גמל</strong> מלשון <strong>גמול</strong><a href=\"http://localhost/tnk1/kma/qjrim1/gmul.html\"> = מידה כנגד מידה</a>;&#160;&#160; <strong>גמלך רעה</strong>\r\n = עשה לך רעה, באותה מידה שאתה רוצה לעשות לו עכשיו. לפי זה, הפסוק מלמד \r\nשהמריבה צריכה להיות מידתית - מותר לריב רק עם אדם שפגע בנו, אבל עוצמת \r\nהמריבה צריכה להיות פרופורציונלית לעוצמת הפגיעה, כך שיהיה אפשר להגדיר \r\nאותה כ\"גמול\".</p>', `dquyot` = '<p>הפסוק אוסר לריב עם אדם <strong>חינם</strong>, ומסביר באיזה מקרה הריב הוא חינם - כאשר הצד השני <strong>לא גמלך רעה</strong>, כלומר מותר לריב רק עם אדם שגמל לנו רעה. למה בדיוק הכוונה?</p><p>1. מותר לריב רק עם אדם שפגע בנו: \"<q class=\"mfrj\"><strong>אל תריב עם אדם</strong>&#160;- להתלונן עליו,&#160;<strong>אם לא גמלך רעה</strong>&#160;- שעבר על המצוה הכתובה בתורה \"ואהבת לרעך כמוך\"; ומי שהוא רשע - רשאי אתה לשונאו</q>\"&#160;<small>(<a href=\"http://he.wikisource.org/wiki/%D7%A7%D7%98%D7%A2:%D7%A8%D7%A9%22%D7%99_%D7%A2%D7%9C_%D7%9E%D7%A9%D7%9C%D7%99_%D7%92_%D7%9C\" class=\"external text\">רש\"י</a></small><small>)</small>.&#160; \"<q class=\"mfrj\">גם תחילת המעשה נקרא במקרא בלשון גמול, וכן</q>\" <a class=\"psuq\" href=\"http://tora.us.fm/tnk1/prqim/t1404.htm#4\">יואל ד4</a>: \"<q class=\"psuq\">וְאִם <strong>גֹּמְלִים</strong> אַתֶּם עָלָי</q>\" <small>(</small><small><a href=\"http://he.wikisource.org/wiki/%D7%A7%D7%98%D7%A2:%D7%9E%D7%A6%D7%95%D7%93%D7%95%D7%AA_%D7%A2%D7%9C_%D7%9E%D7%A9%D7%9C%D7%99_%D7%92_%D7%9C\" class=\"external text\">מצודות</a>)</small>.</p><p>2. מותר לריב רק עם אדם שניסה לפגוע בנו בתחום הרוחני, להחטיא אותנו: \"<q class=\"mfrj\"><strong>אל תריב עם אדם</strong>, אף אם הזיקך ממון,&#160;<strong>אם לא גמלך רעה</strong>, והיינו שהחטיאך. שכל המחטיא חברו כאילו הורגו</q>\"&#160;<small>(<a href=\"/daat/mahshevt/kitsur/10.htm\">קיצור ספר חרדים, פרק י: מצות לא תעשה מדרבנן התלויים בדבור פה</a>)</small>.</p><p>3. ולענ\"ד, <strong>גמל</strong> מלשון <strong>גמול</strong><a href=\"/tnk1/kma/qjrim1/gmul.html\"> = מידה כנגד מידה</a>;&#160;&#160; <strong>גמלך רעה</strong> = עשה לך רעה, באותה מידה שאתה רוצה לעשות לו עכשיו. לפי זה, הפסוק מלמד שהמריבה צריכה להיות מידתית - מותר לריב רק עם אדם שפגע בנו, אבל עוצמת המריבה צריכה להיות פרופורציונלית לעוצמת הפגיעה, כך שיהיה אפשר להגדיר אותה כ\"גמול\".</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 3 AND `verse_number` = 30 AND 1 LIMIT 1;

