UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<div style=\"background:#eef\">\r\n<p> -  - פסוק                                   <strong class=\"selflink\">לה</strong></p>\r\n</div>\r\n\r\n<p><strong>ינחלו</strong> - נוחלים לעצמם ה<strong>כבוד</strong>.</p>\r\n<p><strong>מרים</strong> - כל אחד מפריש ה<strong>קלון</strong> לחלקו.</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>מרים</strong> - מלשון תרומה והפרשה.</p>\r\n<p><strong>קלון</strong> - בזיון, והוא מלשון ():</span> <strong>קלוי באש</strong>, כי בעבור הבזיון יתאדם הפנים כאלו נקלה באש. או הוא מלשון קַלּוּת.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 3 AND `verse_number` = 35 AND 1 LIMIT 1;

