UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p>העושר אשר <strong>בבית הרשע</strong>, <strong>מארת ה\'</strong> (קללת ה\') היא, כי שמורה לרעתו, ותחשב <span class=\"word_with_explain\">אם-כן </span>לקללה; &#160; <strong>ו</strong>ה\' <strong>יברך</strong> את אשר ב<strong>נוה</strong> (מדור)<strong> הצדיקים</strong>, כלומר: לברכה תחשב, כי ישפיעו מטובם לדל ויקבלו גמול.</p>', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 3 AND `verse_number` = 33 AND 1 LIMIT 1;

