UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p>ה\' משלם לכל אחד גמול במידה שמדד: <strong>אם ללצים</strong>, המלעיגים בבני אדם, יסבב סיבה אשר <strong>יתלוצצו</strong> בהם בבוא יום אידם; &#160; <strong>ולענוים</strong>, אשר הם שפלי הרוח ומכבדים כל אדם, <strong>יתן חנם</strong> בעיני הכל, ויכבדו להם.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 3 AND `verse_number` = 34 AND 1 LIMIT 1;

