UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>בבן מביש</strong> - <strong>בבן</strong> האדון, אשר הוא מלא מדברי <strong>בושה</strong>.</p>\r\n<p><strong>יחלוק נחלה</strong> - העבד הזה יקח <strong>חלק נחלה בתוך האחים</strong> כאלו היה אחד מהם.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<br />', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 2 AND 1 LIMIT 1;

