SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>מצרף</strong> - <strong>הכסף והזהב</strong>, כאשר מי ירצה לדעת כמה יש בהם מן הסיגים, יקח ממנו במשקל ידוע, וישימו ב<strong>כור</strong>, והסיג ישרף ונשאר הכסף והזהב נקי, ובזה יבחן הכל. אבל <strong>לבות</strong> בני אדם, <strong>יבחנם ה\'</strong> הטובה היא אם רעה.</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>מצרף וכור</strong> - שמות הכלים אשר יתיכו בהם הזהב והכסף.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `main_qod_subjects` = NULL, `mqorot` = '<br />' WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 3 AND 1 LIMIT 1;

SET character_set_client=utf8;
SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>מרע</strong> - איש <strong>רע מקשיב</strong> לשמוע <strong>דבר און</strong> ושקר, וכן בהפוך, כי איש <strong>שקר</strong>ן <strong>מאזין</strong> לשמוע <strong>לשון הוות</strong> לשבור בני אדם ולהרע להם. ור\"ל אהובים המה זה לזה ולא יתפרדו.</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>מזין</strong> - כמו מאזין, ר\"ל מקשיב באזנו.</p>\r\n<p><strong>הוות</strong> - ענין שבר ורעה, כמו ():</span> <strong>מדבר הוות</strong>.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<br />', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 4 AND 1 LIMIT 1;

SET character_set_client=utf8;
SET character_set_connection=utf8;
UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>חרף עושהו</strong> - כי בלעגו כאילו יאמר, שלחסרון דעתו העני ולא ה\' פעל כל זאת.</p>\r\n<p><strong>שמח לאיד</strong> - ה<strong>שמח</strong> על תקלת חבירו <strong>לא ינקה</strong> מן התקלה ההיא, כי תבוא גם עליו.</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>לאיד</strong> - ענין תקלה ומקרה רע, כמו ():</span> <strong>הלא איד לעוול</strong>.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `mqorot` = '<br />' WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 5 AND 1 LIMIT 1;

