UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>עבד משכיל ימשול</strong> <strong>בבן</strong> האדון, אשר הוא מלא מדברי <strong>בושה</strong>;&#160;&#160; <strong>ו</strong>העבד הזה יקח <strong>חלק נחלה בתוך האחים</strong>, כאלו היה אחד מהם.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 2 AND 1 LIMIT 1;

