UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '<p><strong>הכסף והזהב</strong>, כאשר מי ירצה לדעת כמה יש בהם מן הסיגים, יקח ממנו במשקל ידוע, וישימו ב<strong>מצרף וכור</strong>, והסיג ישרף ונשאר הכסף והזהב נקי, ובזה יבחן הכל; &#160;&#160; אבל <strong>לבות</strong> בני אדם, <strong>יבחנם ה\'</strong> הטובה היא אם רעה.</p>', `main_qod_subjects` = NULL WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 3 AND 1 LIMIT 1;

