UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>לא נאה</strong> בעיני ה<strong>נבל</strong> (אדם פחות ונבזה) דברי מעלה ו<strong>יתרון</strong>, ולא יחפוץ בהם. וכל שכן ש<strong>לא נאה שפת שקר</strong> בעיני מי ש<strong>נדבה</strong> לבו בחכמה, כי יותר ימאס החכם בשקר משימאס הנבל בדברי מעלה.</p>', `tirgum` = '<p>\r\n<strong>לא נאה</strong>    אפילו לאדם \r\n<strong>נבל</strong> (גס והמוני) להבטיח \r\n<strong>בשפתיו יותר</strong>    ממה שיעשה בפועל;&#160;&#160; <strong>אף כי</strong>    (קל וחומר), אין זה נאה לאדם \r\n<strong>נדיב</strong>, נעלה ונכבד, להבטיח \r\n<strong>בשפתיו</strong>    דברי \r\n<strong>שקר</strong>    שלא יעשה כלל.</p><p>/ <strong>לא רצוי</strong> להגיד על אדם <strong>נבל</strong> שיש לו <strong>יתרונות</strong> ומעלות שאין בו;&#160;&#160; <strong>אף כי</strong> (קל וחומר) אין זה רצוי להגיד על אדם <strong>נדיב</strong> דברי-גנאי שהם <strong>שקר</strong>.</p>', `mqorot` = '<br />', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 7 AND 1 LIMIT 1;

