UPDATE `sgulot` SET `prefix` = NULL, `mcudot` = '\r\n\r\n<p><strong>מכסה</strong> - אם יחטא איש לאיש והוא <strong>מכסה על פשעיו</strong> ולא יזכירם, הנה בזה <strong>מבקש אהבה</strong>, ר\"ל היא סיבה להיות מתאהב על הבריות.</p>\r\n<p><strong>ושונה בדבר</strong> - המספר הפשע ו<strong>שונה</strong> בה לספרה שנית לבל ישכחנה, <strong>מפריד</strong> מעצמו <strong>אלופ</strong>ו של עולם, כי יעבור על \"לא תטור\".</p>\r\n<h3><span class=\"mw-headline\" id=\".D7.9E.D7.A6.D7.95.D7.93.D7.AA_.D7.A6.D7.99.D7.95.D7.9F\">מצודת ציון</span></h3>\r\n<p><strong>ושונה</strong> - מלשון שנית.</p>\r\n<p><strong>אלוף</strong> - שר ומושל.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', `tosfot` = NULL WHERE `book` = 'משל' AND `chapter_number` = 17 AND `verse_number` = 9 AND 1 LIMIT 1;

